function orbitalPeriod(arr) {
  var GM = 398600.4418;
  var earthRadius = 6367.4447;
  for (var obj of arr) {
    var a = earthRadius+obj.avgAlt;
    var T = Math.round(2*Math.PI*Math.sqrt(Math.pow(a, 3)/GM));
    delete obj.avgAlt
    obj.orbitalPeriod = T;
  }
return arr
}

