function palindrome(str) {
  // Good luck!
  let newstr = str.split(' ').join('').replace(/\W|_/g,'').toLowerCase();
  //console.log(newstr);
  let flag = 0 ;
  var x = newstr.length;
  for (let i=0 ; i < x ; i++) {
    if (newstr[i] === newstr[x-1-i]) {
      flag = 1 ;
    } else {
      flag = 0 ;
      break;
    }console.log(flag)
  }
  if (flag == 1 ){
    return true;
  } else {
    return false;
  }
}



