function smallestCommons(arr) {
  let newarr = []
  for(let i = Math.min(...arr); i <= Math.max(...arr); i++){
    newarr.push(i);
  }
  let ans = 1; 
  for (let i = Math.max(...arr); i >= Math.min(...arr); i--) {
     ans = lcm (ans , i)
  }
  function lcm(a, b){
    return (a*b)/gcd(a, b)
  }
  function gcd(a, b) {
    var R;
    while (a%b > 0){
       R = a%b;
       a = b;
       b = R;
    }
    return b;
  }
  console.log(ans);
  return ans;
}

